  --sidebar-accent-foreground: hsl(140, 60%, 45%);
  --sidebar-border: hsl(205, 25%, 91%);
  --sidebar-ring: hsl(140, 60%, 45%);
  --font-sans: 'Inter', sans-serif;
  --font-serif: 'Montserrat', serif;
  --font-mono: Menlo, monospace;
  --radius: 0.75rem;
  --shadow-2xs: 0px 2px 0px 0px hsl(140, 60%, 45%, 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(140, 60%, 45%, 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(140, 60%, 45%, 0.00), 0px 1px 2px -1px hsl(140, 60%, 45%, 0.00);
  --shadow: 0px 2px 0px 0px hsl(140, 60%, 45%, 0.00), 0px 1px 2px -1px hsl(140, 60%, 45%, 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(140, 60%, 45%, 0.00), 0px 2px 4px -1px hsl(140, 60%, 45%, 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(140, 60%, 45%, 0.00), 0px 4px 6px -1px hsl(140, 60%, 45%, 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(140, 60%, 45%, 0.00), 0px 8px 10px -1px hsl(140, 60%, 45%, 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(140, 60%, 45%, 0.00);
  --tracking-normal: 0em;
  --spacing: 0.25rem;
}

.dark {
  --background: hsl(210, 40%, 8%);
  --foreground: hsl(210, 40%, 95%);
  --card: hsl(210, 40%, 10%);
  --card-foreground: hsl(210, 40%, 95%);
  --popover: hsl(210, 40%, 10%);
  --popover-foreground: hsl(210, 40%, 95%);
  --primary: hsl(140, 60%, 55%);
  --primary-foreground: hsl(210, 40%, 8%);
  --secondary: hsl(210, 40%, 15%);
  --secondary-foreground: hsl(210, 40%, 95%);
  --muted: hsl(210, 40%, 15%);
  --muted-foreground: hsl(210, 40%, 65%);
  --accent: hsl(140, 40%, 20%);
  --accent-foreground: hsl(140, 60%, 55%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(210, 40%, 20%);
  --input: hsl(210, 40%, 20%);
  --ring: hsl(140, 60%, 55%);
  --strawberry: hsl(0, 75%, 65%);
  --blueberry: hsl(220, 70%, 60%);
  --sidebar: hsl(210, 40%, 10%);
  --sidebar-foreground: hsl(210, 40%, 95%);
  --sidebar-primary: hsl(140, 60%, 55%);
  --sidebar-primary-foreground: hsl(210, 40%, 8%);
  --sidebar-accent: hsl(140, 40%, 20%);
  --sidebar-accent-foreground: hsl(140, 60%, 55%);
  --sidebar-border: hsl(210, 40%, 20%);
  --sidebar-ring: hsl(140, 60%, 55%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

.font-display {
  font-family: var(--font-serif);
}

.animate-fade-in {
  animation: fadeIn 0.6s ease-out;
}

@keyframes fadeIn {
  from { 
    opacity: 0; 
    transform: translateY(20px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

.hover-lift {
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.hover-lift:hover {
  transform: translateY(-4px);
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
}

.gradient-primary {
  background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(140, 50%, 55%) 100%);
}

.gradient-strawberry {
  background: linear-gradient(135deg, hsl(var(--strawberry)) 0%, hsl(15, 75%, 60%) 100%);
}

.gradient-blueberry {
  background: linear-gradient(135deg, hsl(var(--blueberry)) 0%, hsl(240, 70%, 60%) 100%);
}

.form-input {
  transition: all 0.3s ease;
}

.form-input:focus {
  box-shadow: 0 0 0 3px hsla(var(--ring) / 0.2);
  border-color: hsl(var(--ring));
}

.section-padding {
  padding: 4rem 0;
}

@media (max-width: 768px) {
  .section-padding {
    padding: 2.5rem 0;
  }
}

