  MenubarContent,
  MenubarItem,
  MenubarSeparator,
  MenubarLabel,
  MenubarCheckboxItem,
  MenubarRadioGroup,
  MenubarRadioItem,
  MenubarPortal,
  MenubarSubContent,
  MenubarSubTrigger,
  MenubarGroup,
  MenubarSub,
  MenubarShortcut,
}

client/src/components/Header.tsx.content:
import { useState, useEffect } from "react";
import { useLanguage } from "@/hooks/useLanguage";
import { Button } from "@/components/ui/button";
import { Menu, X, Leaf } from "lucide-react";

export default function Header() {
  const { language, setLanguage, t } = useLanguage();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 100);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const scrollToSection = (sectionId: string) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: "smooth", block: "start" });
    }
    setIsMenuOpen(false);
  };

  return (
    <header
      className={`fixed top-0 left-0 right-0 bg-white/100 border-b border-border z-50 transition-shadow duration-300 ${
        isScrolled ? "shadow-lg" : ""
      }`}
    >
      <div className="container mx-auto px-4 py-4">
        <div className="flex items-center justify-between">
          {/* Logo */}
          <div className="flex items-center space-x-3" data-testid="logo">
            <div className="w-10 h-10 gradient-primary rounded-lg flex items-center justify-center">
              <Leaf className="text-primary-foreground text-lg" />
            </div>
            <div>
              <h1 className="text-xl font-display font-bold text-foreground">
                Gerbofru
              </h1>
              <p className="text-xs text-muted-foreground">
                Intermediación Hortofrutícola
              </p>
            </div>
          </div>

          {/* Navigation Menu - Desktop */}
          <nav className="hidden lg:flex items-center space-x-8">
            <button
              onClick={() => scrollToSection("inicio")}
              className="text-foreground hover:text-primary transition-colors"
              data-testid="nav-home"
            >
              {t("nav.home")}
            </button>
            <button
              onClick={() => scrollToSection("nosotros")}
              className="text-foreground hover:text-primary transition-colors"
              data-testid="nav-about"
            >
              {t("nav.about")}
            </button>
            <button
              onClick={() => scrollToSection("productos")}
              className="text-foreground hover:text-primary transition-colors"
              data-testid="nav-products"
            >
              {t("nav.products")}
            </button>
            <button
              onClick={() => scrollToSection("servicios")}
              className="text-foreground hover:text-primary transition-colors"
              data-testid="nav-services"
            >
              {t("nav.services")}
            </button>
            <button
              onClick={() => scrollToSection("contacto")}
              className="text-foreground hover:text-primary transition-colors"
              data-testid="nav-contact"
            >
              {t("nav.contact")}
            </button>
          </nav>

          {/* Language Switcher & Mobile Menu */}
          <div className="flex items-center space-x-4">
            <div className="flex items-center bg-secondary rounded-lg p-1">
              <Button
                variant={language === "es" ? "default" : "ghost"}
                size="sm"
                onClick={() => setLanguage("es")}
                className="px-3 py-1 text-sm font-medium transition-colors"
                data-testid="lang-es"
              >
                ES
              </Button>
              <Button
                variant={language === "en" ? "default" : "ghost"}
                size="sm"
                onClick={() => setLanguage("en")}
                className="px-3 py-1 text-sm font-medium transition-colors"
                data-testid="lang-en"
              >
                EN
              </Button>
            </div>

            {/* Mobile Menu Button */}
            <Button
              variant="ghost"
              size="icon"
              className="lg:hidden"
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              data-testid="mobile-menu-toggle"
            >
              {isMenuOpen ? (
                <X className="h-6 w-6" />
              ) : (
                <Menu className="h-6 w-6" />
              )}
            </Button>
          </div>
        </div>
      </div>

      {/* Mobile Navigation */}
      {isMenuOpen && (
        <div className="lg:hidden bg-card border-t border-border">
          <div className="container mx-auto px-4 py-4 space-y-4">
            <button
              onClick={() => scrollToSection("inicio")}
              className="block w-full text-left text-foreground hover:text-primary transition-colors"
              data-testid="mobile-nav-home"
            >
              {t("nav.home")}
            </button>
            <button
              onClick={() => scrollToSection("nosotros")}
              className="block w-full text-left text-foreground hover:text-primary transition-colors"
              data-testid="mobile-nav-about"
            >
              {t("nav.about")}
            </button>
            <button
              onClick={() => scrollToSection("productos")}
              className="block w-full text-left text-foreground hover:text-primary transition-colors"
              data-testid="mobile-nav-products"
            >
              {t("nav.products")}
            </button>
            <button
              onClick={() => scrollToSection("servicios")}
              className="block w-full text-left text-foreground hover:text-primary transition-colors"
              data-testid="mobile-nav-services"
            >
              {t("nav.services")}
            </button>
            <button
              onClick={() => scrollToSection("contacto")}
              className="block w-full text-left text-foreground hover:text-primary transition-colors"
              data-testid="mobile-nav-contact"
            >
              {t("nav.contact")}
            </button>
          </div>
        </div>
      )}
    </header>
  );
}

client/src/components/Services.tsx.content:
import { motion } from "framer-motion";
import { useLanguage } from "@/hooks/useLanguage";
import { TrendingUp, Handshake, Truck, ShieldCheck } from "lucide-react";

export default function Services() {
  const { t } = useLanguage();

  const services = [
    {
      icon: TrendingUp,
      title: t("services.commercial_opportunities.title"),
      description: t("services.commercial_opportunities.description"),
      gradient: "from-green-500 to-emerald-600",
      iconColor: "text-white"
    },
    {
      icon: Handshake,
      title: t("services.agreements.title"),
      description: t("services.agreements.description"),
      gradient: "from-blue-500 to-cyan-600",
      iconColor: "text-white"
    },
    {
      icon: Truck,
      title: t("services.logistics.title"),
      description: t("services.logistics.description"),
      gradient: "from-purple-500 to-pink-600",
      iconColor: "text-white"
    },
    {
      icon: ShieldCheck,
      title: t("services.trust.title"),
      description: t("services.trust.description"),
      gradient: "from-orange-500 to-red-600",
      iconColor: "text-white"
    }
  ];

  return (
    <section id="servicios" className="section-padding bg-secondary/50">
      <div className="container mx-auto px-4">
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          <h2 className="text-3xl md:text-4xl font-display font-bold mb-4" data-testid="services-title">
            {t("services.title")}
          </h2>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto" data-testid="services-description">
            {t("services.description")}
          </p>
        </motion.div>

        <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
          {services.map((service, index) => {
            const IconComponent = service.icon;
            return (
              <motion.div 
                key={index}
                className="bg-card rounded-2xl shadow-lg hover-lift overflow-hidden group border border-primary/10 flex flex-col h-full"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                data-testid={`service-card-${index}`}
              >
                {/* Icon header with gradient */}
                <div className={`bg-gradient-to-br ${service.gradient} p-6 relative overflow-hidden`}>
                  <div className="absolute inset-0 bg-black/10 group-hover:bg-black/0 transition-colors duration-300"></div>
                  <div className="w-16 h-16 bg-white/20 backdrop-blur-sm rounded-xl flex items-center justify-center transform group-hover:scale-110 group-hover:rotate-6 transition-all duration-300 relative z-10">
                    <IconComponent className={service.iconColor} size={32} />
                  </div>
                </div>

                {/* Content */}
                <div className="p-6 flex-grow">
                  <h3 className="text-xl font-display font-bold mb-3 group-hover:text-primary transition-colors" data-testid={`service-title-${index}`}>
                    {service.title}
                  </h3>
                  <p className="text-muted-foreground leading-relaxed" data-testid={`service-description-${index}`}>
                    {service.description}
                  </p>
                </div>

                {/* Bottom accent line */}
                <div className={`h-1 bg-gradient-to-r ${service.gradient} mt-auto`}></div>
              </motion.div>
            );
          })}
        </div>
      </div>
    </section>
  );
}

